package com.dxc.ssi.agent.wallet.indy.model.issue

import com.dxc.ssi.agent.didcomm.model.issue.data.CredentialDefinition
import kotlinx.serialization.SerialName
import kotlinx.serialization.Serializable

/**
 * Represents credential definition
 *
 * Example:
 * {
 *  "ver":"1.0",
 *  "id":"V4SGRU86Z58d6TV7PBUe6f:3:CL:11:TAG_1",
 *  "schemaId":"11",
 *  "type":"CL",
 *  "tag":"TAG_1",
 *  "value":{
 *      "primary":{
 *          "n":"",
 *          "s":"",
 *          "rms":"",
 *          "r":{
 *              "sex":"",
 *              "age":"",
 *              "height":"",
 *              "name":""
 *          },
 *          "rctxt":"",
 *          "z":""
 *      }
 *  }
 * }
 */
@Serializable
data class IndyCredentialDefinition(
    val ver: String,
    val id: String,
    @SerialName("schemaId") val schemaSeqNo: String,
    val type: String,
    val tag: String,
    val value: IndyCredentialPubKeys
) : CredentialDefinition

